import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import AboutMe from "./pages/AboutMe";
import Contact from "./pages/Contact";
import Portfolio from "./pages/Portfolio";
import MyNavbar from "./components/MyNavbar";
import "./App.css";
import Container from "react-bootstrap/Container";
import SideBar from "./components/Sidebar";
import MainContainer from "./components/MainContainer";
import SideAccordion from "./components/SideAccordion";
import MyFooter from "./components/Footer";

function App() {

  function changeSideBar(event) {
    setSideBarState({ title: event.title, titleItem: event.titleItem });
  }

  function sideNavCheck() {
    if (
      window.location.pathname === "/" ||
      window.location.pathname === "/aboutme"
    ) {
      changeSideBar({
        title: "Background",
        titleItem: [
          {
            link: "#from",
            linkTitle: "Where I Am From",
          },
          {
            link: "#education",
            linkTitle: "Education",
          },
          {
            link: "#family",
            linkTitle: "My Family",
          },
        ],
      });
    } else if (window.location.pathname === "/contact") {
      changeSideBar({
        title: "Contacts",
        titleItem: [
          {
            link: "",
            linkTitle: "Email",
          },
          {
            link: "github",
            linkTitle: "Github Profile",
          },
          {
            link: "linkin",
            linkTitle: "LinkIn Profile",
          },
        ],
      });
    } else if (window.location.pathname === "/portfolio") {
      changeSideBar({
        title: "Portfolio",
        titleItem: [
          {
            link: "#proj1",
            linkTitle: "Project 1",
          },
          {
            link: "#proj2",
            linkTitle: "Project 2",
          },
          {
            link: "#proj3",
            linkTitle: "Project 3",
          },
          {
            link: "#proj4",
            linkTitle: "Project 4",
          },
          {
            link: "#proj5",
            linkTitle: "Project 5",
          },
          {
            link: "#proj6",
            linkTitle: "Project 6",
          },
        ],
      });
    }
  }

  return (
    <Router>
      {sideNavCheck}
      <div style={{ scrollPaddingTop: "60px" }}>
        <MyNavbar />
        <Switch>
          <Route exact path="/" component={AboutMe}>
            <SideBar>
              <SideAccordionAbout
                title={sideBarState.title}
                titleItem={sideBarState.titleItem}
              />
            </SideBar>
            <Container fluid>
              <MainContainer></MainContainer>
            </Container>
          </Route>
          <Route exact path="/contact" component={Contact}>
            <SideBar>
              <SideAccordionContact
                title={sideBarState.title}
                titleItem={sideBarState.titleItem}
              />
            </SideBar>
            <Container fluid>
              <MainContainer></MainContainer>
            </Container>
          </Route>
          <Route exact path="/portfolio" component={Portfolio}>
            <SideBar>
              <SideAccordionPortfolio
                title={sideBarState.title}
                titleItem={sideBarState.titleItem}
              />
            </SideBar>
            <Container fluid>
              <MainContainer></MainContainer>
            </Container>
          </Route>
          <Route exact path="/aboutme" component={AboutMe}>
            <SideBar>
              <SideAccordion
                title={sideBarState.title}
                titleItem={sideBarState.titleItem}
              />
            </SideBar>
            <Container fluid>
              <MainContainer></MainContainer>
            </Container>
          </Route>
        </Switch>
        <MyFooter />
      </div>
    </Router>
  );
}

export default App;
